function validarMensaje(){if(nombres=$("#nombres").val(),apellidos=$("#apellidos").val(),mensaje=$("#mensaje").val(),""!=nombres){nombres.length;if(!/^[a-zA-Z\s]*$/.test(nombres))return $("#nombres").after('<div class="alert alert-warning">No se permiten números ni caracteres especiales.</div>'),!1}if(""!=apellidos){apellidos.length;if(!/^[a-zA-Z\s]*$/.test(apellidos))return $("#apellidos").after('<div class="alert alert-warning">No se permiten números ni caracteres especiales.</div>'),!1}else if(""!=mensaje){mensaje.length;if(!/^[a-zA-Z0-9\s]*$/.test(mensaje))return $("#mensaje").after('<div class="alert alert-warning">No se permiten caracteres especiales.</div>'),!1}return!0}